package com.xkcoding.test.test44;

import cn.hutool.core.util.RandomUtil;
import cn.hutool.extra.template.Template;
import cn.hutool.extra.template.TemplateConfig;
import cn.hutool.extra.template.TemplateEngine;
import cn.hutool.extra.template.TemplateUtil;
import com.google.common.base.Charsets;
import lombok.SneakyThrows;
import org.docx4j.Docx4J;
import org.docx4j.fonts.IdentityPlusMapper;
import org.docx4j.fonts.Mapper;
import org.docx4j.fonts.PhysicalFonts;
import org.docx4j.openpackaging.packages.WordprocessingMLPackage;

import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileOutputStream;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * <p>
 * 测试 模板->word->PDF
 * </p>
 *
 * @author yangkai.shen
 * @date Created in 2020/2/11 15:31
 */
public class Test44 {
    public static void main(String[] args) {
        Test44 test44 = new Test44();
        test44.generate("/Users/yangkai.shen/Desktop/aaa.pdf");
    }

    @SneakyThrows
    public void generate(String fileName) {
        Map<String, Object> data = initData();

        processImage(data, "chart0", "chart0.png");

        processImage(data, "chart1", "chart1.png");

        WordprocessingMLPackage wmlPackage = generateDocx(data);

        generatePDF(wmlPackage, fileName);
    }

    @SneakyThrows
    private void generatePDF(WordprocessingMLPackage wmlPackage, String fileName) {
        FileOutputStream os = new FileOutputStream(new File(fileName));

        processFonts(wmlPackage);

        // FOSettings foSettings = Docx4J.createFOSettings();
        // foSettings.setWmlPackage(wmlPackage);
        // Docx4J.toFO(foSettings, os, Docx4J.FLAG_EXPORT_PREFER_XSL);

        Docx4J.toPDF(wmlPackage, new FileOutputStream(fileName));

        System.out.println("PDF 生成成功！" + fileName);
    }

    @SneakyThrows
    private void processFonts(WordprocessingMLPackage wmlPackage) {
        Mapper fontMapper = new IdentityPlusMapper();
        fontMapper.put("隶书", PhysicalFonts.get("LiSu"));
        fontMapper.put("宋体", PhysicalFonts.get("SimSun"));
        fontMapper.put("微软雅黑", PhysicalFonts.get("Microsoft Yahei"));
        fontMapper.put("黑体", PhysicalFonts.get("SimHei"));
        fontMapper.put("楷体", PhysicalFonts.get("KaiTi"));
        fontMapper.put("新宋体", PhysicalFonts.get("NSimSun"));
        fontMapper.put("华文行楷", PhysicalFonts.get("STXingkai"));
        fontMapper.put("华文仿宋", PhysicalFonts.get("STFangsong"));
        fontMapper.put("仿宋", PhysicalFonts.get("FangSong"));
        fontMapper.put("幼圆", PhysicalFonts.get("YouYuan"));
        fontMapper.put("华文宋体", PhysicalFonts.get("STSong"));
        fontMapper.put("华文中宋", PhysicalFonts.get("STZhongsong"));
        fontMapper.put("等线", PhysicalFonts.get("SimSun"));
        fontMapper.put("等线 Light", PhysicalFonts.get("SimSun"));
        fontMapper.put("华文琥珀", PhysicalFonts.get("STHupo"));
        fontMapper.put("华文隶书", PhysicalFonts.get("STLiti"));
        fontMapper.put("华文新魏", PhysicalFonts.get("STXinwei"));
        fontMapper.put("华文彩云", PhysicalFonts.get("STCaiyun"));
        fontMapper.put("方正姚体", PhysicalFonts.get("FZYaoti"));
        fontMapper.put("方正舒体", PhysicalFonts.get("FZShuTi"));
        fontMapper.put("华文细黑", PhysicalFonts.get("STXihei"));
        fontMapper.put("宋体扩展", PhysicalFonts.get("simsun-extB"));
        fontMapper.put("仿宋_GB2312", PhysicalFonts.get("FangSong_GB2312"));
        fontMapper.put("新細明體", PhysicalFonts.get("SimSun"));
        //解决宋体（正文）和宋体（标题）的乱码问题
        PhysicalFonts.put("PMingLiU", PhysicalFonts.get("SimSun"));
        PhysicalFonts.put("新細明體", PhysicalFonts.get("SimSun"));
        wmlPackage.setFontMapper(fontMapper);
    }

    @SneakyThrows
    private WordprocessingMLPackage generateDocx(Map<String, Object> data) {
        final TemplateEngine engine = TemplateUtil.createEngine(new TemplateConfig("test44", TemplateConfig.ResourceMode.CLASSPATH));
        final Template template = engine.getTemplate("template.xml");
        final String doc = template.render(data);

        return WordprocessingMLPackage.load(new ByteArrayInputStream(doc.getBytes(Charsets.UTF_8)));
    }

    @SneakyThrows
    private void processImage(Map<String, Object> data, String key, String name) {
        String base64Info = String.valueOf(data.get(key));
        base64Info = base64Info.split("base64,")[1];

        Map<String, Object> image = new HashMap<>(2);
        image.put("name", name);
        image.put("data", base64Info);

        data.put(key, image);
    }

    private Map<String, Object> initData() {
        Map<String, Object> data = new HashMap<>(4);

        data.put("chart0", "data:image/png;base64,");
        data.put("chart1", "data:image/png;base64,");

        List<Map<String, Object>> dataList = new ArrayList<>();
        for (int i = 0; i < 100; i++) {
            Map<String, Object> item = new HashMap<>(4);
            item.put("index", i + 1);
            item.put("name", "student" + i);
            item.put("score", RandomUtil.randomInt(80, 100));
            dataList.add(item);
        }
        data.put("table", dataList);

        return data;
    }


}
